	public function run(): void
	{
		PageText::query()->truncate();
		Page::query()->truncate();

		foreach ($this->pages as $pageData) {
			$page = new Page();
			$page->slug = $pageData['slug'];
			$page->save();

			foreach ($pageData['texts'] as $text) {
				$pageText = new PageText();
				$pageText->locale = $text['locale'];
				$pageText->short_title = $text['short_title'];
				$pageText->title = $text['title'];
				$pageText->description = $text['description'];
				$pageText->seo_title = $text['seo_title'];
				$pageText->seo_description = $text['seo_description'];
				$pageText->sections = $text['sections'];

				if (Arr::get($text, 'seo_keywords')) {
					$pageText->seo_keywords = $text['seo_keywords'];
				}

				$pageText->page()->associate($page);
				$pageText->save();
			}

			foreach (Arr::get($pageData, 'files', []) as $fileData) {
				$file = new File();
				$file->path = $fileData['path'];
				$file->type = $fileData['type'];

				$file->fileable()->associate($page);
				$file->save();
			}
		}
	}
